start mainorg 5010label loc_fizzdata string "Fizz"org 5020label loc_buzzdata string "Buzz"	org 2label println	const 10	out	retlabel puts	ent 1label puts_loop		ldr 0	mva	const 0	ne	brf puts_else	ldr 0	out	lda 0	inc	sta 0	br puts_looplabel puts_else	retlabel fizzbuzz	ent 2	const 1	sto 2label fizzbuzz_start		ldo 2	mva	ldo 1	le	brf fizzbuzz_else		const 0	sto 3	ldo 2	mva	const 3	mod	mva	const 0	eq	brf fizzbuzz_next1	const 1	sto 3	const loc_fizz	sta 0	call puts 0	const 32	outlabel fizzbuzz_next1		ldo 2	mva	const 5	mod	mva	const 0	eq	brf fizzbuzz_next2	const 1	sto 3	const loc_buzz	sta 0	call puts 0	const 32	outlabel fizzbuzz_next2		ldo 3	mva	const 0	eq	brf fizzbuzz_last	ldo 2	printlabel fizzbuzz_last	ldo 2	inc	sto 2	call println 0	br fizzbuzz_startlabel fizzbuzz_else	retlabel main	const 100	push	call fizzbuzz 1	call println 0	call println 0	halt